requests.description:
【请求参数】

'search_name' //搜索的地区名称

例如：'search_name' = '中';

【成功返回】

{

  "status": 1,   //0表示失败，1表示成功

  "message": "",//返回文字信息， 你们想返回什么文字可和我说

  "data": [     //返回数据   返回城市，不包括省

    {

      "region_id": "95",    //地区id

      "parent_id": "6",     //父id  表示该地区的上一级地区的id

      "region_name": "中山",//地区名称

      "region_type": "2"    //地区类型，1表示省，2表示城市/区 3表示县

    },

    {

      "region_id": "132",

      "parent_id": "9",

      "region_name": "琼中",

      "region_type": "2"

    },

【失败返回】

{

  "status": 0,   //返回类型，0表示失败，1表示成功

  "message": "没有该城市，请重新输入！",        //返回的文字，需要返回什么按app需求

  "data": ""    //返回数据

}
